		<node Folded="true" ID="ID_1827139579" TEXT="Difference between Security and Privacy">
		
 <attribute NAME="Type" VALUE="syllabus_point" />
		<node ID="ID_109591041" TEXT="Difference between Security and Privacy#$D$#">
